frappe.ui.form.on("Purchase Receipt", "items_on_form_rendered", function(frm) {
        // Important Note : Sub Form Fieldname+"_on_form_rendered" would trigger and add
        // The button in child form
	var grid_row = cur_frm.open_grid_row();

        // Add Button to Child Form ... Wrap it around the "dialog_result" field
        var $btn = $('<button class="btn btn-sm btn-default">'+__("Add Serial No")+'</button>')
                .appendTo($("<div>")
		.css({"margin-bottom": "10px", "margin-top": "10px"})
		.appendTo(grid_row.fields_dict.serial_no.$wrapper));

        // Bind a Event to Added Button
        $btn.on("click", function() {
        var fields = [];
        for (var rows=1;rows<=grid_row.fields_dict.received_qty.get_value()+1;rows++) {
            fields.push({label:"Row No:".concat(rows), fieldname: "row_no_".concat(rows), fieldtype: "Section Break"});
            for (var columns=1;columns<=3;columns++) {
                if (columns == 1) {
                    fields.push({label:"Serial/Chassis No", fieldname: "chassis_no_".concat(rows), fieldtype: "Data", reqd:1});
                }
		if (grid_row.fields_dict['is_vehicle'].value==1) {
			if (columns == 2) {
	                    fields.push({label:"Engine No", fieldname: "engine_no_".concat(rows), fieldtype: "Data", reqd:1});
	                }
	                if (columns == 3) {
        	            fields.push({label:"Key No", fieldname: "key_no_".concat(rows), fieldtype: "Data", reqd:1});
	                	}
        	        if (columns < 3) {
	                    fields.push({fieldname: "column_no_".concat(columns), fieldtype: "Column Break"});
                	}
		}
            }
        }
        // Add "Add" Button to the Dialog
        fields.push({fieldtype: "Section Break"});
        //fields.push({fieldtype: "Button", "label": "Set", fieldname: "add"});


			var d = new frappe.ui.Dialog({
				title: __("Add Serial No"),
                                fields
			});

               if (grid_row.fields_dict.serial_no.get_value()!="") {
                    var existing_serial_no = grid_row.fields_dict.serial_no.get_value().split("\n");
                    var existing_engine_no = grid_row.fields_dict.serial_no.get_value().split("\n");
                    var existing_key_no = grid_row.fields_dict.serial_no.get_value().split("\n");
                    for (var row=1;row<=existing_serial_no.length+1;row++) {
                             d.set_value("chassis_no_".concat(row),existing_serial_no[row-1]);
                             d.set_value("engine_no_".concat(row),existing_engine_no[row-1]);
                             d.set_value("key_no_".concat(row),existing_key_no[row-1]);
                    }
               }
                        //Bind Event to Add Button in Dialog
			d.set_primary_action(__("Save"), function() {
/*****************/
		args = d.get_values();
		if (!args) return;
		frappe.call({
			method:"mapl_customization.customizations_for_mapl.utils.validate_input_serial",
			args : { "args": d.get_values(),
                                 "rows": grid_row.fields_dict.received_qty.get_value()+1,
                                 "is_vehicle": grid_row.fields_dict['is_vehicle'].value
                                },

			callback: function (r) {
				if (!r.message) {
                                   //return;
				}
			}
		});

/*****************/

			//d.get_input("add").on("click", function() {
                            var result_serial_no = "";
                            var result_engine_no = "";
                            var result_key_no = "";
                                    for (var rows=1;rows<=grid_row.fields_dict.received_qty.get_value()+1;rows++) {
                                            result_serial_no = result_serial_no.concat(d.get_value("chassis_no_".concat(rows)),"\n");
                                    //Set Resultant Value from Dialog in a Child Form Field - Here dialog_result
                                    grid_row.fields_dict.serial_no.set_model_value(result_serial_no);
                                            result_engine_no = result_engine_no.concat(d.get_value("engine_no_".concat(rows)),"\n");
                                    //Set Resultant Value from Dialog in a Child Form Field - Here dialog_result
                                    grid_row.fields_dict.engine_nos.set_model_value(result_engine_no);
                                            result_key_no = result_key_no.concat(d.get_value("key_no_".concat(rows)),"\n");
                                    //Set Resultant Value from Dialog in a Child Form Field - Here dialog_result
                                    grid_row.fields_dict.key_nos.set_model_value(result_key_no);
                         }
				d.hide();
				return false;
			});

			d.show();
		});
        });
/***************************************************************/
/*              End Dialog On Child Form                       */
/***************************************************************/

