frappe.ui.form.on("Sales Invoice", "shipping_address_name", function(frm) {
    if (!frm.doc.shipping_address_name) {
       frm.set_value("shipping_address",null);
    }
});
frappe.ui.form.on("Sales Invoice", "is_finance", function (frm) {
         cur_frm.set_df_property("hypothecation", "reqd", frm.doc.is_finance==1);
         cur_frm.refresh_field("hypothecation");
});
frappe.ui.form.on("Sales Invoice", "customer", function (frm) {
        cur_frm.set_df_property("customer_name", "hidden", false);
             if (frm.doc.customer) {
		frappe.call({
			method: "mapl_customization.customizations_for_mapl.utils.get_party_balance",
			args: {
				party: frm.doc.customer,
				company: frm.doc.company,
                                party_type: "Customer"
			},
			callback: function(r) {
				if(!r.exc && r.message) {
                                    var alert_msg = frm.doc.customer+" Current Balance:";
                                    if (parseFloat(r.message) > 0) {
                                          alert_msg = alert_msg+"<b><span style=\"color:red;\">"+parseFloat(r.message)+" Dr.</span></b>";
                                    } else if (parseFloat(r.message) < 0) {
                                          alert_msg = alert_msg+"<b><span style=\"color:blue;\">"+parseFloat(r.message)+" Cr.</span></b>";
                                    } else {
                                          alert_msg = alert_msg+"<b><span style=\"color:black;\"> 0.00</span></b>";
                                    }
					  					  show_alert(alert_msg, 8);
				} else {
                                          show_alert(frm.doc.customer+" Current Balance:<b><span style=\"color:black;\"> 0.00</span></b>",8);
                                    }
			}
		});          
            }
});

