<style>
    html{
        height:100%;
        position:relative
    }
    body{
        min-height:100% /* force the BODY element to match the height of the HTML element */
    }
    .print-format td {
        padding: 0px !important;
    }
    .print-format {
        height: 100%;
        background-color: initial;
        box-shadow: initial;
        max-width: initial;
        min-height: initial;
        margin: initial;
        padding: initial !important;        
    }
    .print-format-gutter {
        height: 100%;
    }
    @media screen {
        .page-preview {
            min-height: {{ min_page_height }};
            max-width: {{ max_page_width }};
            padding: {{ page_padding }};
            background-color: white;
            box-shadow: 0px 0px 9px rgba(0,0,0,0.5);
            margin: auto;
            display: table;
            width: 100%;
        }
    }
    @media print {   
    	@page {
            size: A5 landscape;
            margin: {{ page_padding }};
        }
        body {
            height: 100%;
            -webkit-print-color-adjust: exact !important;
        }
        .page-preview {
            height: 100%;
            width: 100%;
            max-height: {{ max_page_height }}
            display: table;
        }
        body .img {
            width: auto !important;
            height: auto !important;
            max-width: auto !important;
        }
    }
</style>



<!-- Macro for Header -->
{% macro setheader() -%}
<div style="width: 100%;height:100%;">
    <table style="width: 100%;height:100%">
        {% if letter_head and not no_letterhead -%}
        <tr>
            <td colspan="100%" style="width: 100%;padding-bottom: 3px !important;">
                {{ letter_head }}	
            </td>
        </tr>
        <tr><td colspan="100%"><hr style="margin-top:0px;margin-bottom:0px;border:none;color:black;background:black;height:1px;"></hr></td></tr>
        {%- endif %}
        <tr style="height:100%;">
            <td colspan="100%" style="width: 100%;padding: 0px !important;">
                <div style="height:65px;max-height: 65px;overflow: hidden;padding-top:3px;background: #eee;padding-bottom: 3px;">
                    <table style="width: 100%;height:100%;">
                        <tr>
                            <td style="height: 100%;width: 20%;color: black;font-size: 9px;text-transform: uppercase;text-align: left;vertical-align: middle !important;">
                                <!-- INTENTIONALY LEFT BLANK -->
                            </td>					
                            <td style="width: 60%;color: black;text-align: center;vertical-align: middle !important;">
                                <span style="font-size: 22px;text-transform:uppercase;"><B>{{ doc.select_print_heading or "Receipt" }}</B></span>
                            </td>					
                            <td style="height: 100%;width: 20%;color: black;font-size: 12px;text-transform: uppercase;text-align: right;vertical-align: middle !important;">
                                <B>{{ doc.name }} <br> Date: {{ doc.get_formatted("receipt_date") }} </br> </B>
                            </td>					
                        </tr>
                    </table>
                </div>
                <table style="border-collapse: collapse;width: 100%;margin-top:2px;">
                    <tr>
                        <td style="width: 50%;vertical-align: center;">
                            <table style="width: 100%;font-size:11px;">
                                <tr> 
                                    <td>
                                        <span style="color:red;font-size: 11px;">Name</span>
                                    </td>
                                    <td style="width: 75%">
                                        <span style="font-size: 11px;text-align: left;"><B> {{ frappe.db.get_value(doc.party_type, doc.party, "customer_name") }} </B></span>
                                    </td>
                                </tr>
                            </table>
                        </td>
                        <td style="width: 50%;" rowspan="100%">
                            {% if doc.party_address -%}
                            <table style="width: 100%;font-size:10px;">
                                <tr> 
                                    <td style="width: 30%">
                                        <span style="color:red;">Address</span>
                                    </td>
                                    <td style="width: 70%">
                                        <B>{{ doc.address }}</B>
                                    </td>
                                </tr>
                            </table>
                            {%- endif %}
                        </td>                        
                    </tr>
                </table>
            </td>
        </tr>
    </table>
</div>
{%- endmacro %}
<!-- End Macro -->


<!-- Footer Macro -->
{% macro setfooter(islast) -%}
<!-- Important Note: Using Position Relative Here and Position Absolute in Table 
 Would Anchor Table to the Bottom of DIV -->
<div style="height:100%;width:100%;position:relative;">       
     <div style="width: 100%;background: #eee;height:60%;max-height:60%;bottom:0px;position:absolute;">
	     <table style="width: 100%;height: 100%;">
         	<tr><td colspan="100%"><hr style="margin-top:0px;margin-bottom:0px;border:none;color:black;background:black;height:1px;"></hr></td></tr>
	        <tr style="height: 35%;">
                <td style="width:50%;text-align:left;"><span style="padding-left:10px;">Cheque Payment(s) are Subject to Realization</span></td> 
	            <td style="height: 100%;width: 100%; text-align: right;">
	                <span style="padding-right:10px;">For {{ doc.company }}</span>
	            </td>
	        </tr>
	        <tr>
	            <td style="height: 20%;width: 100%; text-align: right;" colspan="100%">
	                <span style="padding-right:10px;">Manager</span>
	            </td>
	        </tr>
	    </table>
    </div>
</div>
{%- endmacro %}
<!-- End Macro -->


{% macro do_body() -%}		
	<table style="width:100%;height:100%;">
    	<tr>
        	<td style="width:70%;padding-bottom: 20px !important;">
            	Received with Thanks <b> {{ doc.in_words }} </b>
            </td>
        	<td style="width:30%;height:100%;vertical-align:middle !important;text-align: right;" rowspan="100%">
            	<span style="font-size:16px;font-weight:800;"> {{ doc.get_formatted("payment_amount") }} </span>
            </td>            
        </tr>
        	<tr><td>Mode of Payment: <b>{{ doc.payment_mode }}</b></td></tr>
            {% if doc.payment_mode != "Cash" %}
            	<tr><td>Instrument No: {{ doc.instrument_reference }}</td></tr>
                <tr><td>Instrument Bank : {{ doc.instrument_bank }}</td></tr>
                <tr><td>Instrument Date: {{ doc.get_formatted("instrument_date") }}</td></tr>
            {% endif %}
    </table>
{%- endmacro %}

