{% macro break_token(token,row) -%}
	{% set index = token.find(':') %}
	{% set model = token[1:index] %}
	{% set tax = token[index+2:] %}

	{% set model = model[:model.find('"')] %}
	{% set tax = tax[:tax.find(']')] %}

    {% set tax_percentage = tax[:tax.find(',')] %}
	{% set tax_amount = tax[tax.find(',')+1:] %}
	{# {{ "Model :" + model + "   Tax:" + tax + "    Tax Per:" + tax_percentage + "  Tax Amt:"+ tax_amount }}  #}

	{% if tax_percentage|float > 0 %}
		<td style="width:40%;padding: 1px !important;"><div style="max-height: 10px;overflow: hidden;"> {{ model }} </div></td>
		<td style="width:60%;padding: 1px !important;">
        	<div style="width:30%;max-height: 10px;overflow: hidden;float:left;"> 
            	({{ tax_percentage }}%) 
            </div>
            <div style="width:70%;max-height: 10px;overflow: hidden;float:left;text-align: right !important;">
            	{{ frappe.format_value(tax_amount,row.meta.get_field('tax_amount')) }}  
            </div>
        </td>
    {% endif %}
{%- endmacro %}

{% macro print_taxes_charges() -%}	
    <table style="width: 100%;height: 100px;">
    	{% if print_tax_analysis %}
		<tr style="font-size: 9px;height:10px;max-height:10px;">									 
			<td style="width: 35%;padding: 1px !important;float:left;"><div style="max-height: 10px;overflow: hidden;"><B> Tax Analysis </B></div></td>
		</tr>    
        {% endif %}
		<tr>
        	<!-- DIFFERENT TAX ANALYSIS -->
			<td style="width: 65%;float:left;display:flex;">            	
                {% if print_tax_analysis %}
                <!-- LINE COUNTER -->
                {% set line_counter = ['1'] %}
                <!-- START A NEW SUB DIV -->
                <div style="float:left;width:32%;">
				{% for taxes in doc.taxes %}                
					{% if taxes.charge_type != 'Actual' and taxes.base_tax_amount!=0 %}
						<table style="width: 100%;">
							<tr style="font-size: 8px;max-height:10px !important;color:red;">									   
								<td style="width: 40%;padding: 1px !important;"><div style="max-height: 10px;overflow: hidden;"> Item </div></td>
								<td style="width: 60%;padding: 1px !important;"><div style="max-height: 10px;overflow: hidden;">  {{ taxes.account_head }} </div></td>									
							</tr>
							{% set item_taxes = taxes.item_wise_tax_detail %}         
							{% set main_token = item_taxes[1:item_taxes.find('}')] %}
							{% for i in range(10000) if index > 0 %}
                            	<!-- LINE COUNTER -->
                    			{% if line_counter.append('1') %}{% endif %}
								<tr style="font-size: 7px;">
									{% set index = main_token.find('],') %}
									<!-- {# {{ "Main Token:" + main_token }}  #} -->
									{% if index < 0 %}
										{% set token = main_token %}
										<!-- {# {{ "Token:" + token }} #} -->
										{{ break_token(token,taxes) }}
									{% else %}     
										{% set index = main_token.find('],') %}
										{% set token = main_token[:index+1] %}
										{% set main_token = main_token[index+2:] %}
										<!-- {# {{ "Token:" + token }} #} -->
										{{ break_token(token,taxes) }}
									{% endif %}
								</tr>
                                <!-- LOGIC FOR BREAKING TAX ANALYSIS INTO PARTS -->
			                    {% if line_counter|length >= 6 %}
			                        <!-- END OLD DIV -->
			                  	      	</table>
                                      </div>
                        			<!-- START A NEW DIV -->
                        			  <div style="float:left;padding-left:2px;border-left:1px gray solid;width:32%;">
                                      	<table style="width:100%;">
                        			{% set line_counter = ['1'] %}
                    			{% endif %}                                       
							{% endfor %}
						</table>
					{% endif %}
				{% endfor %}
                </div>
                {% endif %}
            </td>
            <!-- DIFFRENT VAT & CHARGES PRINT -->
			<td style="width: 25%;padding: 0px 50px 0px 0px;float:right;" rowspan="100%">
				<table cellpadding="0" cellspacing="0" style="width: 100%;font-size:10px;margin-top:3px !important;">
					{%- for row in doc.taxes -%}
                        {% if row.tax_amount > 0 %}
						<tr>
							<td style="width: 60%;padding: 1px !important;">
								<div style="max-height: 11px;overflow: hidden;">
									{{ row.description }}
								</div>
							</td>
							<td class="text-right" style="width: 40%;padding: 1px !important;">
								<div style="max-height: 11px;overflow: hidden;">
									{{ row.get_formatted("tax_amount", doc) }}
								</div>
							</td>
						</tr>
                        {% endif %}
					{%- endfor -%}
				</table>
			</td>                        
		</tr>
	</table>
{%- endmacro %}

