{% macro do_body() -%}		
	            <table style="width: 100%;height: 100%;font-size:9px;">

                <tbody>

                    <!-- Initialize Logic to Count Total Number of Rows -->
                    {% set total_lines = [] %}

					<!-- VERTICAL LINES -->
                    <tr><td colspan="100%"><hr style="margin-top:0 !important;margin-bottom:0 !important;border-top:1px solid gray;"></hr></td></tr>
					<tr>
                    	<td style="width=5%;"><!-- INTENTIONALLY LEFT BLANK --></td>
                        <td style="border-right: 1px solid gray;" rowspan="100%"></td>
                    	<td style="width=49%;"><!-- INTENTIONALLY LEFT BLANK --></td>
                        <td style="border-right: 1px solid gray;" rowspan="100%"></td>
                    	<td style="width=10%;"><!-- INTENTIONALLY LEFT BLANK --></td>
                        <td style="border-right: 1px solid gray;" rowspan="100%"></td>
                    	<td style="width=18%;"><!-- INTENTIONALLY LEFT BLANK --></td>
                        <td style="border-right: 1px solid gray;" rowspan="100%"></td>
					</tr>

                    {%- for row in doc.items -%}

                        {% if loop.index % 2 == 0 -%}
                        <!-- Do Something / Alternative Rows -->	   
                        {%- endif %}

                        <tr style="min-height: {{ row_height }}px;height: {{ row_height }}px;border-bottom:1px solid #eee;">
                            <td style="width: 5%;padding-left:4px !important;padding-top:3px !important;">{{ row.idx }} </td>
                            <td style="width: 49%;padding-left:4px !important;padding-top:3px !important;">                        
                                {{ row.item_name }}
                                {% if row.item_code != row.item_name -%}
                                    <br>Item Code: {{ row.item_code}}</br>
                                {%- endif %}
                                <br><I><span style="color:blue;">{{ row.description }} </span></I></br>
                                {% if row.serial_no != None -%}
                                    <span style="color:brown;"> 
                                        {%- for serial in row.serial_no.split('\n') -%}   
                                            {% if serial != '' %}
                                                {% set serialdoc = frappe.get_doc("Serial No",serial) %}
                                                <br>{{ serialdoc.serial_no }} {{ serialdoc.purchase_time }}</br>
                                            {%- endif %}
                                        {%- endfor %}
                                    </span>
                                {%- endif %}				
                            </td>
                            <td style="width: 10%; text-align: right;padding-right:4px !important;padding-top:3px !important;">{{ row.get_formatted("qty", doc) }} {{ row.uom or row.stock_uom }}</td>
                            <td style="width: 18%; text-align: right;padding-right:4px !important;padding-top:3px !important;">{{ row.get_formatted("base_rate", doc) }}</td>
                            <td style="width: 18%; text-align: right;padding-right:4px !important;padding-top:3px !important;">{{ row.get_formatted("base_amount", doc) }}</td>                            
                        </tr>

	                <!-- Page Break Logic Here -->
	                {% if loop.index % display_rows_per_page == 0  and loop.index < doc.items|length %}
		                <!-- Fill Empty Space -->
		                <tr style="height: 100%"> 
		                    <td> </td>
		                </tr>  
                        <tr><td colspan="100%"><hr style="margin-top:0 !important;margin-bottom:0 !important;border-top:1px solid gray;"></hr></td></tr>

						<!-- Need to End the Inner Blocks (Table, TBody) Used Above -->
						</tbody></table>
							<!-- Page Break -->
							{{ insert_page_break(true) }}
						<!-- Start New Inner Block -->
						<table style="width: 100%;height: 100%;font-size:9px;"><tbody>
						<!-- VERTICAL LINES -->
                        <tr><td colspan="100%"><hr style="margin-top:0 !important;margin-bottom:0 !important;border-top:1px solid gray;"></hr></td></tr>
						<tr>
	                    	<td style="width=5%;"><!-- INTENTIONALLY LEFT BLANK --></td>
	                        <td style="border-right: 1px solid gray;" rowspan="100%"></td>
	                    	<td style="width=49%;"><!-- INTENTIONALLY LEFT BLANK --></td>
	                        <td style="border-right: 1px solid gray;" rowspan="100%"></td>
	                    	<td style="width=10%;"><!-- INTENTIONALLY LEFT BLANK --></td>
	                        <td style="border-right: 1px solid gray;" rowspan="100%"></td>
	                    	<td style="width=18%;"><!-- INTENTIONALLY LEFT BLANK --></td>
	                        <td style="border-right: 1px solid gray;" rowspan="100%"></td>
						</tr>

	                {% endif %}
                    <!-- End Page Break Logic -->

                {% endfor %}

                <!-- Fill Empty Space -->
                <tr style="height: 100%"> 
                    <td> </td>
                </tr>            
				<tr><td colspan="100%"><hr style="margin-top:0 !important;margin-bottom:0 !important;border-top:1px solid gray;"></hr></td></tr>

                <!-- End Table -->
                </tbody>
            </table>        
{%- endmacro %}

