css:
.print-format-container {
    position: absolute !important;
    height:100%;
    width:100%;
}
.print-preview {
    position: relative !important;
}

html:
{% set display_rows_per_page = 3 %}
{% set row_height = 40 %}
{% set div_page_height = "5.25in" %}
{% set div_max_page_height = "5.25in" %}
{% set min_page_height = "5.25in" %}
{% set min_page_width = "8.3in" %}
{% set max_page_height = "11.69in" %}
{% set max_page_width = "8.3in" %}
{% set preferred_page_height = "11.69in" %}
{% set preferred_page_width = "8.3in" %}
{% set footer_background = "transparent" %}
{% set header_background = "transparent" %}
{% set body_background = "transparent" %}
{% set header_height = "45%" %}
{% set footer_height = "20%" %}
{% set body_height = "initial" %}
{% set page_padding = "1cm" %}



<style>
    body{
        min-height:100% /* force the BODY element to match the height of the HTML element */
    }
    .print-format td {
        padding: 0px !important;
    }
    .print-format {
        height: 100%;
        background-color: initial;
        box-shadow: initial;
        max-width: initial;
        min-height: initial;
        margin: initial;
        padding: initial !important;
    }
    .print-format-gutter {
        height: 100%;
    }
    @media screen {
        .page-preview {
            min-height: {{ min_page_height }} !important;
            max-width: {{ max_page_width }} !important;
            padding: {{ page_padding }};
            background-color: white;
            margin: auto;
            display: table !important;
            width: 100%;
        }
        .print-format.print-format-preview > .page-preview {
            position: absolute;
        }
    }
    @media print {   
    	html{
	        height:100%;
	        position:relative
	    }    
    	@page {
            //size: A5 landscape;
            margin: {{ page_padding }};
        }
        body {
            height: 100%;
            -webkit-print-color-adjust: exact !important;
        }
        .page-preview {
            height: 100%;
            width: 100%;
            max-height: {{ max_page_height }}
            display: table;
        }
        body .img {
            width: auto !important;
            height: auto !important;
            max-width: auto !important;
        }
    }
</style>



<!-- Macro for Header -->
{% macro setheader() -%}
<div style="width: 100%;height:100%;">
    <table style="width: 100%;height:100%">
        {% if letter_head and not no_letterhead -%}
        <tr>
            <td colspan="100%" style="width: 100%;padding-bottom: 3px !important;background:white !important;">
                {{ letter_head }}	
            </td>
        </tr>
        <tr><td colspan="100%"><hr style="margin-top:0px;margin-bottom:0px;border:none;color:black;background:black;height:1px;"></hr></td></tr>
        {%- endif %}
        <tr style="height:100%;">
            <td colspan="100%" style="width: 100%;padding: 0px !important;">
                <div style="height:65px;max-height: 65px;overflow: hidden;padding-top:3px;background: #eee !important;padding-bottom: 3px;">
                    <table style="width: 100%;height:100%;">
                        <tr>
                            <td style="height: 100%;width: 20%;color: black;font-size: 12px;text-transform: uppercase;text-align: left;vertical-align: middle !important;padding-left:5px !important;">
                                <B>{{ doc.reporting_name or doc.name }}</B>
                            </td>					
                            <td style="width: 60%;color: black;text-align: center;vertical-align: middle !important;">
                                <span style="font-size: 22px;text-transform:uppercase;"><B>{{ doc.select_print_heading or "Receipt" }}</B></span>
                            </td>					
                            <td style="height: 100%;width: 20%;color: black;font-size: 12px;text-transform: uppercase;text-align: right;vertical-align: middle !important;padding-right:5px !important;">
                                <B>Date: {{ doc.get_formatted("posting_date") }}</B>
                            </td>					
                        </tr>
                    </table>
                </div>
                <table style="border-collapse: collapse;width: 100%;margin-top:10px;margin-left:5px;margin-right:5px;">
                    <tr>
                        <td style="width: 40%;vertical-align: center;">
                            <table style="width: 100%;font-size:13px;">
                                <tr> 
                                    <td style="width: 14%;">
                                        <span style="color:red;font-size: 13px;">Name</span>
                                    </td>
                                    <td style="width: 100%">
                                        <span style="font-size: 13px;text-align: left;"><B> {{ doc.party_name }} </B></span>
                                    	{% set coname = frappe.db.get_value("Customer", doc.party, "company_name") %}
                                        {% if coname %}
                                    		<span style="font-size:12px;text-align: left;"><br/><B>C/O {{ coname }}</B></span>
                                        {% endif %}                                        
                                        <span style="font-size: 13px;text-align: left;"><br/>Phone:{{ frappe.db.get_value("Customer",doc.party,"mobile_no") }}</span>
                                    </td>
                                </tr>
                            </table>
                        </td>
                        <td style="width: 100%;" rowspan="100%">
                            {% if doc.party_address -%}
                            <table style="width: 100%;font-size:13px;">
                                <tr> 
                                    <td style="width: 15%">
                                        <span style="color:red;">Address</span>
                                    </td>
                                    <td style="width: 100%">
                                        <B>{{ doc.address }}</B>
                                    </td>
                                </tr>
                            </table>
                            {%- endif %}
                        </td>                        
                    </tr>
                </table>
            </td>
        </tr>
    </table>
</div>
{%- endmacro %}
<!-- End Macro -->


<!-- Footer Macro -->
{% macro setfooter(islast) -%}
<!-- Important Note: Using Position Relative Here and Position Absolute in Table 
 Would Anchor Table to the Bottom of DIV -->
<div style="height:100%;width:100%;position:relative;">       
     <div style="width: 100%;background: #eee !important;height:60%;max-height:60%;bottom:0px;position:absolute;">
	     <table style="width: 100%;height: 100%;">
         	<tr><td colspan="100%"><hr style="margin-top:0px;margin-bottom:0px;border:none;color:black;background:black;height:1px;"></hr></td></tr>
	        <tr style="height: 35%;">
                <td style="width:50%;text-align:left;"><span style="padding-left:10px;">Cheque Payment(s) are Subject to Realization</span></td> 
	            <td style="height: 100%;width: 100%; text-align: right;">
	                <span style="padding-right:10px;">For {{ doc.company }}</span>
	            </td>
	        </tr>
	        <tr>
	            <td style="height: 20%;width: 100%; text-align: right;" colspan="100%">
	                <span style="padding-right:10px;">Manager</span>
	            </td>
	        </tr>
	    </table>
    </div>
</div>
{%- endmacro %}
<!-- End Macro -->


{% macro do_body() -%}		
	<table style="width:100%;height:100%;margin-top:20px;margin-left:5px;margin-right:5px;">
    	<tr>
        	<td style="width:70%;padding-bottom: 20px !important;">
            	Received with Thanks <b> {{ doc.in_words }} </b>
            </td>
        	<td style="width:30%;height:100%;vertical-align:middle !important;text-align:right;padding-right:20px !important;" rowspan="100%">
            	<span style="font-size:16px;font-weight:800;"> {{ doc.get_formatted("paid_amount") }} </span>
            </td>            
        </tr>
        	{% set base_mode = frappe.db.get_value("Mode of Payment", doc.mode_of_payment, "base_mode") %}
        	<tr><td style="padding-top:40px !important;">Mode of Payment: <b>{{ base_mode }}</b></td></tr>
            {% if doc.base_mode != "Cash" %}
            	{% if doc.reference_no %}
            		<tr><td>Instrument/Batch No: {{ doc.reference_no }}</td></tr>
                {% endif %}
                {% if doc.cheque_bank %}
                	<tr><td>Instrument Bank : {{ doc.cheque_bank }}</td></tr>
                {% endif %}
                {% if doc.reference_date %}
                	<tr><td>Instrument Date: {{ doc.get_formatted("reference_date") }}</td></tr>
                {% endif %}
            {% endif %}
    </table>
{%- endmacro %}

