<!-- Don't Edit the Following Blocks -->
<!-- Instead Use The Macros Above -->
<!--------------------------------------------------------------------------------------------------->


{% macro insert_page_break(test_condition) -%}
	{% if test_condition %} 

		                <!-- Fill Empty Space -->
		                <div style="height: 100%"></div>

			<!-- End Body Container -->
		        </div>

		    <!-- End Main Body Div -->  
		    </div>

		    <!-- Set Footer -->
		    <div style="max-height: {{ footer_height }};height: {{ footer_height }};background: {{ footer_background }};;overflow: hidden;">
		        {{ setfooter(false) }}
		    </div>


		<!-- End Main Div -->
		</div>


		<!-- PAGE BREAK -->
		<div class="page-break"></div> <!-- Seems to be working in Chrome Ver: 38.0.2125.111 & Chrome 43 -->

		<!-- Logic to Count Total Number of Pages -->
		{% if total_pages.append('1') %} {% endif %}	

		<!-- Start Another Main Div Tag -->
		<div class="page-preview" style="height: {{ div_page_height }};max-height: {{ div_max_page_height }};">    

		    <!-- Set New Header -->
		    <div style="max-height: {{ header_height }};height: {{ header_height }};background: {{ header_background }};overflow: hidden;">
				<div style="width: 100%;height:100%;">
					{{ setheader() }}
				</div>
		    </div>


		    <!-- Start a New Body -->
		    <div style="max-height: {{ body_height }};height: {{ body_height }};background: {{ body_background }};overflow: hidden;">

		        <!-- New Body Container -->
		        <div style="width:100%;height: 100%;">	

	{% endif %}
{%- endmacro %}


<!-- Main Div Tag -->
<div class="page-preview" style="height: {{ div_page_height }};max-height: {{ div_max_page_height }};display:flex !important;flex-direction:column;">

	<!-- Logic to Count Total Number of Pages -->
	{% set total_pages = [1] %}

	<!-- Dummy Div for Background -->
    <div style="height:100%;width:100%;display:flex;flex-direction:column;flex:1;">

    <!-- Set Header -->
    <div style="background: {{ header_background }};padding-bottom:20px;">
		<div style="width: 100%;height:100%;">
			{{ setheader() }}
		</div>
    </div>

    <!-- Set Body -->
    <!-- Main Body Tag -->
    <div style="max-height: {{ body_height }};height: {{ body_height }};background: {{ body_background }};overflow: hidden;flex-grow:1;">

        <!-- Body Container -->
        <div style="width:100%;height: 100%;">

        	{{ do_body() }}

        <!-- End Body Container Div -->
        </div>

        <!-- End Main Body Div -->            
    </div>


    <!-- Set last Footer -->
    <div style="max-height: {{ footer_height }};height: {{ footer_height }};background: {{ footer_background }};;overflow: hidden;">
		<div style="width: 100%;height: 100%;"> 
			{{ setfooter(true) }}
		</div>
    </div>

    </div>

</div>

